import pandas as pddf = pd.read_csv(r'Traindat2.csv')df2 = []for i in range(1,80):    batt = 'NO.{}'.format(i)    rul = 'RUL{}'.format(i)    for j in range(0,2058):        s = [df.at[j,'Cycle'], i, df.at[j, batt], df.at[j, rul]]        df2.append(s)        df3 = pd.DataFrame(df2, columns=['Cycle', 'Battnum', 'Capacity', 'RUL'])df3.to_csv('Traindatlong.csv', index = False)